import { Link } from "react-router-dom";
import { useState } from "react";
import { toast } from "react-toastify";
import axios from "axios";
import jsPDF from "jspdf";
import "jspdf-autotable";

const Reports = () => {
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [PassengertoDate, setPassegngerToDate] = useState("");
  const [PassengerfromDate, setPassengerFromDate] = useState("");
  const [DriverfromDate, setDriverFromDate] = useState("");
  const [DrivertoDate, setDriverToDate] = useState("");
  const [VehiclefromDate, setVehicleFromDate] = useState("");
  const [VehicletoDate, setVehicleToDate] = useState("");
  const [RevenuefromDate, setRevenueFromDate] = useState("");
  const [RevenuetoDate, setRevenueToDate] = useState("");
  const [isDownloading, setIsDownloading] = useState(false);

  const handleGenerateReport = (reportType) => {
    if(!fromDate || !toDate){
        toast.error("Please select from and to date");
    }
    console.log(`Generating ${reportType} report from ${fromDate} to ${toDate}`);
  };

  const handleGeneratePassengerReport = (reportType) => {
    if(!PassengerfromDate || !PassengertoDate){
        return toast.error("Please select from and to date");
    }

    axios.get(`http://localhost:3000/api/v1/reports/total-passengers?fromDate=${PassengerfromDate}&toDate=${PassengertoDate}`)
    .then((res) => {
      if(res.data.message.length === 0){
        return toast.error("No data found for the selected date range");
      }
      generatePDF(res.data.message);
    })
    .catch((err) => {
        toast.error(err.response.data.message);
    });
    console.log(`Generating ${reportType} report from ${PassengerfromDate} to ${PassengertoDate}`);
  }

  const generatePDF = (data) => {
    setIsDownloading(true);
    const doc = new jsPDF('landscape');
    const pageWidth = doc.internal.pageSize.getWidth();
  
    const img = "data:image/png;base64,"; // Replace with your Base64 image
    doc.addImage(img, "PNG", pageWidth / 2 - 15, 10, 30, 30); 
  
    doc.setFontSize(18);
    const title = "City Taxi Passengers Report";
    const titleWidth = doc.getTextWidth(title);
    doc.text(title, (pageWidth - titleWidth) / 2, 50);
  
    doc.setFontSize(12);
    const reportDateText = `Date: ${new Date().toLocaleDateString()}`;
    const reportDateWidth = doc.getTextWidth(reportDateText);
    doc.text(reportDateText, (pageWidth - reportDateWidth) / 2, 60);
  
    const dateRangeText = `From: ${PassengerfromDate} To: ${PassengertoDate}`;
    const dateRangeWidth = doc.getTextWidth(dateRangeText);
    doc.text(dateRangeText, (pageWidth - dateRangeWidth) / 2, 70);
  
    const tableColumn = [
      "ID",
      "Full Name",
      "Email",
      "Username",
      "NIC",
      "Phone",
      "Address",
      "Registered Operator",
      "Registered At",
    ];
    const tableRows = [];
  
    data.forEach((passenger) => {
      const passengerData = [
        passenger.id,
        passenger.fullName,
        passenger.email,
        passenger.username,
        passenger.nic,
        passenger.phone,
        passenger.address,
        passenger.operator ? passenger.operator.fullName : "N/A",
        new Date(passenger.createdAt).toLocaleDateString(),
      ];
      tableRows.push(passengerData);
    });
  
    // Generate the table with jsPDF AutoTable
    doc.autoTable({
      head: [tableColumn],
      body: tableRows,
      startY: 80,
      columnStyles:{
        0: {cellWidth: 10}
      },
    });
  
    // Save the PDF with the current date in the filename
    const today = new Date();
    const formattedDate = `${today.getFullYear()}-${(today.getMonth() + 1)
      .toString()
      .padStart(2, "0")}-${today.getDate().toString().padStart(2, "0")}`;
    doc.save(`passenger_report_${formattedDate}.pdf`);
    setIsDownloading(false);
  };
  


  return (
    <div className="card shadow" style={{ marginBottom: "150px" }}>
      <div className="card-header py-3 d-sm-flex align-items-center justify-content-between">
        <Link to="/dashboard">
          <i className="fas fa-arrow-left"></i>{" "}
        </Link>
        <h6 className="m-0 font-weight-bold text-primary">Reports</h6>
        <div>{""}</div>
      </div>
      <div className="card-body">
        <div className="table-responsive">
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Report Type</th>
                <th>From Date</th>
                <th>To Date</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Ride Report</td>
                <td>
                  <input
                    type="date"
                    value={fromDate}
                    onChange={(e) => setFromDate(e.target.value)}
                    className="form-control"
                  />
                </td>
                <td>
                  <input
                    type="date"
                    value={toDate}
                    onChange={(e) => setToDate(e.target.value)}
                    className="form-control"
                  />
                </td>
                <td>
                  <button
                    className="btn btn-primary"
                    onClick={() => handleGenerateReport("Total Rides")}
                  >
                    Generate Report
                  </button>
                </td>
              </tr>
              <tr>
                <td>Passenger Report</td>
                <td>
                  <input
                    type="date"
                    value={PassengerfromDate}
                    onChange={(e) => setPassengerFromDate(e.target.value)}
                    className="form-control"
                  />
                </td>
                <td>
                  <input
                    type="date"
                    value={PassengertoDate}
                    onChange={(e) => setPassegngerToDate(e.target.value)}
                    className="form-control"
                  />
                </td>
                <td>
                  <button
                    className="btn btn-primary"
                    onClick={() => handleGeneratePassengerReport("Ride Status")}
                    disabled={isDownloading}
                  >
                   {
                      isDownloading ? "Please wait..." : "Generate Report"
                   }
                  </button>
                </td>
              </tr>
              <tr>
                <td>Driver Report</td>
                <td>
                  <input
                    type="date"
                    value={fromDate}
                    onChange={(e) => setDriverFromDate(e.target.value)}
                    className="form-control"
                  />
                </td>
                <td>
                  <input
                    type="date"
                    value={toDate}
                    onChange={(e) => setDriverToDate(e.target.value)}
                    className="form-control"
                  />
                </td>
                <td>
                  <button
                    className="btn btn-primary"
                    onClick={() => handleGenerateReport("Rides by Driver")}
                  >
                    Generate Report
                  </button>
                </td>
              </tr>
              <tr>
                <td>Vehicle Report</td>
                <td>
                  <input
                    type="date"
                    value={fromDate}
                    onChange={(e) => setVehicleFromDate(e.target.value)}
                    className="form-control"
                  />
                </td>
                <td>
                  <input
                    type="date"
                    value={toDate}
                    onChange={(e) => setPassegngerToDate(e.target.value)}
                    className="form-control"
                  />
                </td>
                <td>
                  <button
                    className="btn btn-primary"
                    onClick={() => handleGenerateReport("Rides by Passenger")}
                  >
                    Generate Report
                  </button>
                </td>
              </tr>
             
              <tr>
                <td>Revenue Report</td>
                <td>
                  <input
                    type="date"
                    value={fromDate}
                    onChange={(e) => setRevenueFromDate(e.target.value)}
                    className="form-control"
                  />
                </td>
                <td>
                  <input
                    type="date"
                    value={toDate}
                    onChange={(e) => setRevenueToDate(e.target.value)}
                    className="form-control"
                  />
                </td>
                <td>
                  <button
                    className="btn btn-primary"
                    onClick={() => handleGenerateReport("Rides by Passenger")}
                  >
                    Generate Report
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Reports;
